import { Persona, Encuesta, Registro, personas, encuestas, registros } from './datos';

// Función para mostrar información de personas
export function mostrarPersonas(): void {
  console.log("Personas:");
  personas.forEach(persona => {
    console.log(persona);
  });
}

// Función para mostrar información de encuestas
export function mostrarEncuestas(): void {
  console.log("Encuestas:");
  encuestas.forEach(encuesta => {
    console.log(encuesta);
  });
}

// Función para mostrar información de registros
export function mostrarRegistros(): void {
  console.log("Registros:");
  registros.forEach(registro => {
    console.log(registro);
  });
}

// Función para buscar registro por ID
export function buscarRegistroPorID(id: number): Registro | undefined {
  return registros.find(registro => registro.ID === id);
}
